mapper.select:
select * from board
where subject like CONCAT('%',#{search},'%')
order by num desc limit #{startRow},#{pageSize}

select count(*)
from board

where subject like CONCAT('%',#{search},'%');

mapper.update:
update board
set readcount = #{readcount}
where num = #{num}

update board
set subject = #{subject}, content = #{content}
where num = #{num}

update board
set subject = #{subject}, content = #{content}, file = #{file}
where num = #{num}
